import React, { useState } from "react";

import styles from "./shared/Product.module.css";

import { shorten } from "../helper/functions";
import axios from "axios";

const HazfProduct = ({ productData }) => {
  const products = useContext(ProductsContext);

  const [del,setDel] = useState('')

  const delProducts = async () => {
    const response = await axios.get("https://amir-h.iran.liara.run/products");
    setDel(response.data)
  };


  return (
    <div className={styles.container}>
      <img className={styles.cardImage} src={productData.image} alt="product" />
      <h3>{shorten(productData.title)}</h3>
      <p>{`${productData.price} $`}</p>
      <div className={styles.linkContainer}>
        <div className={styles.buttonHazfContainer}>
          <button onClick={delProducts}>Delete</button>
        </div>
      </div>
    </div>
  );
};

export default HazfProduct;
