import React, { useContext, useState } from "react";
import style from "./Admin.module.css";
import { ProductsContext } from "../context/ProductContextProvider";
import styles from "./Store.module.css";
import HazfProduct from "./HazfProduct";
import AddProduct from "./AddProduct";
import axios from "axios";

const Admin = () => {
  let del = "";
  const [show, setShow] = useState(false);
  const [add, setAdd] = useState(false);
  const [mem, setMem] = useState(false);
  const [admin, setAdmin] = useState(false);
  const products = useContext(ProductsContext);
  const [member, setMember] = useState([]);
  const [alladmin, setAllAdmin] = useState([]);
  const delHandler = () => {
    setShow(!show);
    setAdmin(false);
    setMem(false);
    setAdd(false);
  };
  const addHandler = () => {
    setShow(false);
    setAdmin(false);
    setMem(false);
    setAdd(!add);
  };
  const memHandler = async () => {
    setShow(false);
    setAdmin(false);
    setMem(!mem);
    setAdd(false);
    const members = await axios.get(
      "https://shoping-cart.iran.liara.run/users"
    );
    setMember(members.data);
    console.log(members.data);
  };
  const adminHandler =async () => {
    setShow(false);
    setAdmin(!add);
    setMem(false);
    setAdd(false);
    const admins = await axios.get(
      "https://shoping-cart.iran.liara.run/admins"
    );
  };

  return (
    <div>
      <div className={style.admin}>
        <p>پنل ادمین</p>
        <button onClick={addHandler} className={style.a}>
          اضافه کردن محصول
        </button>
        <button onClick={delHandler} className={style.a}>
          حذف محصول
        </button>
        <button onClick={memHandler} className={style.a}>
          مدیریت کاربران
        </button>
        <button onClick={adminHandler} className={style.a}>
          مدیریت ادمین ها
        </button>
      </div>
      <div className={styles.container}>
        {show === true &&
          products.map((product) => (
            <HazfProduct key={product.id} productData={product} />
          ))}
        {add === true && <AddProduct />}

        {mem === true &&
          member.map((a) => (
            <div key={a._id}>
              <ul>
                <li>{a._id}</li>
                <li>{a.name}</li>
                <li>{a.email}</li>
                <li>{a.password}</li>

                <button
                  className={style.b}
                  onClick={async () => {
                    del = a._id;
                    await axios.delete(
                      `https://shoping-cart.iran.liara.run/users/${del}`
                    );
                    window.location.reload();
                  }}
                >
                  پاک کردن
                </button>
              </ul>
            </div>
          ))}
        {admin === true && <p>{}</p>}
      </div>
    </div>
  );
};

export default Admin;
