import React, { useContext, useState } from "react";
import style from "./Admin.module.css";
import { ProductsContext } from "../context/ProductContextProvider";
import styles from "./Store.module.css";
import HazfProduct from "./HazfProduct";
import AddProduct from "./AddProduct";
import axios from "axios";

const Admin = () => {
  let del = "";
  const [show, setShow] = useState(false);
  const [add, setAdd] = useState(false);
  const [mem, setMem] = useState(false);
  const [admin, setAdmin] = useState(false);
  const products = useContext(ProductsContext);
  const [member, setMember] = useState([]);
  const [allAdmin, setAllAdmin] = useState([]);
  const delHandler = () => {
    setShow(!show);
    setAdmin(false);
    setMem(false);
    setAdd(false);
  };
  const addHandler = () => {
    setShow(false);
    setAdmin(false);
    setMem(false);
    setAdd(!add);
  };
  const memHandler = async () => {
    setShow(false);
    setAdmin(false);
    setMem(!mem);
    setAdd(false);
    const members = await axios.get(
      "https://shoping-cart.iran.liara.run/users"
    );
    setMember(members.data);
  };
  const adminHandler =async () => {
    setShow(false);
    setAdmin(!add);
    setMem(false);
    setAdd(false);
    const admins = await axios.get(
      "https://shoping-cart.iran.liara.run/admins"
    );
    setAllAdmin(admins.data)
  };

  return (
    <div className={style.mainContainer}>
      <div className={style.Container}>


      </div>
 
    </div>
  );
};

export default Admin;
