import React, { useContext, useState } from "react";

// Components
import Product from "./shared/Product";

// Context
import { ProductsContext } from "../context/ProductContextProvider";

// Style
import styles from "./Store.module.css";

const Store = () => {
  const products = useContext(ProductsContext);
  const [search, setSearch] = useState("");
  const searchHandler = (event) => {
    setSearch(event.target.value);
  };
  let searchProduct = products.filter((x) => x.title.includes(search));
  const categoryHandler=()=>{
    if(products.filter((x) => x.category==="men's clothing" ))
     searchProduct = products.filter((x) => x.category.includes("men's clothing"));

  };
  if(products.filter((x) => x.category==="jewelery" )){
    searchProduct = products.filter((x) => x.category.includes("jewelery"));

  }
  return (
    <>
      <input
        className={styles.search}
        type="text"
        placeholder="search"
        value={search}
        onChange={searchHandler}
      />
      <br></br>
      <div className={styles.category}>
        <button onClick={categoryHandler}>men's clothing</button>
        <button onClick={categoryHandler}>jewelery</button>
        <button onClick={categoryHandler}>electronics</button>
        <button onClick={categoryHandler}>women's clothing</button>
        <button onClick={categoryHandler}>All</button>
      </div>

      <div className={styles.container}>
        {searchProduct.map((product) => (
          <Product key={product.id} productData={product} />
        ))}
      </div>
    </>
  );
};

export default Store;
