import React, { Component,useContext } from "react";
import styles from "./Cards.module.css";
import iphonex from "./iPhone-X.jpg";
import iphone11 from "./iphone-11.jpg";
import iphone12 from "./iphone-12.jpg";
import iphone13 from "./iphone-13.jpg";
import left from "../assets/icons/left.svg";
import right from "../assets/icons/right.svg";
import { Link } from "react-router-dom";
import { shorten } from "../helper/functions";
import { CartContext } from "../context/CartContextProvider";


const Cards = ({productsData}) => {
    const {state, dispatch} = useContext(CartContext);

  return (
    <>

    <div className={styles.container} >
        <img className={styles.cardImage} src={productsData.image} alt="product" />
        <h3>{shorten(productsData.title)}</h3>
        <p>{`${productsData.price} $`}</p>
        <div className={styles.linkContainer}>
            <Link to={`/products/${productsData.id}`}>Details</Link>
           
        </div>
    </div>

    </>
    
);
};

export default Cards;













