import React, { useContext, useState } from "react";
import style from "./Admin.module.css";
import { ProductsContext } from "../context/ProductContextProvider";
import styles from "./Store.module.css";
import HazfProduct from "./HazfProduct";
import AddProduct from "./AddProduct";
import axios from "axios";
import { Link } from "react-router-dom";
import { Sidebar } from 'primereact/sidebar';
import { Button } from 'primereact/button';



const Admin = () => {

  let del = "";
  const [show, setShow] = useState(false);
  const [add, setAdd] = useState(false);
  const [mem, setMem] = useState(false);
  const products = useContext(ProductsContext);
  const [member, setMember] = useState([]);
  const [visibleRight, setVisibleRight] = useState(false);
  const customIcons = (
    <React.Fragment>
        <button className="p-sidebar-icon p-link mr-1">
            <span className="pi pi-print" />
        </button>
        <button className="p-sidebar-icon p-link mr-1">
            <span className="pi pi-arrow-right" />
        </button>
    </React.Fragment>
);
  const delHandler = () => {
    setShow(!show);
    setMem(false);
    setAdd(false);
  };
  const addHandler = () => {
    setShow(false);
    setMem(false);
    setAdd(!add);
  };
  const memHandler = async () => {
    setShow(false);
    setMem(!mem);
    setAdd(false);
    const members = await axios.get(
      "https://shoping-cart.iran.liara.run/users"
    );
    setMember(members.data);
  };

  return (
              <div className="card">


                <Sidebar visible={visibleRight} position="right" onHide={() => setVisibleRight(false)}>
                    <h3>Right Sidebar</h3>
                </Sidebar>



                <Button icon="pi pi-arrow-left" onClick={() => setVisibleRight(true)} className="mr-2" />

            </div>  
  )
};

export default Admin;
