import React, { useContext, useState } from "react";

// Components
import Product from "./shared/Product";

// Context
import { ProductsContext } from "../context/ProductContextProvider";

// Style
import styles from "./Store.module.css";

const Store = () => {
  const products = useContext(ProductsContext);
  const [search, setSearch] = useState("");
  const searchHandler = (event) => {
    setSearch(event.target.value);
  };
  let searchProduct = products.filter((x) => x.title.includes(search));
  const categoryHandler1 = () => {
    searchProduct = products.filter((x) =>
      x.Category.includes("men's clothing")
    );
  };
  const categoryHandler2 = () => {
    searchProduct = products.filter((x) => x.Category.includes("jewelery"));
  };
  const categoryHandler3 = () => {
    searchProduct = products.filter((x) => (x.Category = "electronics"));
  };
  const categoryHandler4 = () => {
    searchProduct = products.filter((x) => (x.Category = "women's clothing"));
  };
  console.log(searchProduct);

  return (
    <>
      <input
        className={styles.search}
        type="text"
        placeholder="search"
        value={search}
        onChange={searchHandler}
      />
      <br></br>
      <div className={styles.category}>
        <button onClick={categoryHandler1}>men's clothing</button>
        <button onClick={categoryHandler2}>jewelery</button>
        <button onClick={categoryHandler3}>electronics</button>
        <button onClick={categoryHandler4}>women's clothing</button>
        {/* <button onClick={categoryHandler5}>All</button> */}
      </div>

      <div className={styles.container}>
        {searchProduct.map((product) => (
          <Product key={product.id} productData={product} />
        ))}
      </div>
    </>
  );
};

export default Store;
