/* eslint-disable no-unused-vars */
/* eslint-disable no-undef */
import React, { useContext, useState } from "react";
import style from "./Admin.module.css";
import Product from "./shared/Product";
import { ProductsContext } from "../context/ProductContextProvider";
import styles from "./Store.module.css";
import HazfProduct from "./HazfProduct";
import AddProduct from "./AddProduct";

const Admin = () => {
  const [show, setShow] = useState(false);
  const [add, setAdd] = useState(false);
  const [mem,setMem]=useState(false)
  const [admin,setAdmin]=useState(false)
  const products = useContext(ProductsContext);
  const delHandler = () => {
    setShow(!show);
    setAdmin(!admin);
    setMem(false);
    setAdd(false);
  };
  const addHandler = () => {
    setShow(!show);
    setAdmin(!admin);
    setMem(!mem);
    setAdd(false);
  };
  const memHandler = () => {
    setShow(!show);
    setAdmin(!admin);
    setMem(!mem);
    setAdd(false);
  };
  const adminHandler = () => {
    setShow(!show);
    setAdmin(true);
    setMem(!mem);
    setAdd(!add);
  };

  return (
    <div>
      <div className={style.admin}>
        <p>پنل ادمین</p>
        <button onClick={addHandler} className={style.a}>
          اضافه کردن محصول
        </button>
        <button onClick={delHandler} className={style.a}>
          حذف محصول
        </button>
        <button className={style.a}> مدیریت کاربران</button>
        <button className={style.a}> مدیریت ادمین ها</button>
      </div>
      <div className={styles.container}>
        {show === true &&
          products.map((product) => (
            <HazfProduct key={product.id} productData={product} />
          ))}
        {add === true && <AddProduct />}
      </div>
    </div>
  );
};

export default Admin;
