/* eslint-disable no-unused-vars */
/* eslint-disable no-undef */
import React, { useContext, useState } from "react";
import style from "./Admin.module.css";
import Product from "./shared/Product";
import { ProductsContext } from "../context/ProductContextProvider";
import styles from "./Store.module.css";
import HazfProduct from "./HazfProduct";
import AddProduct from "./AddProduct";
import axios from "axios";

const Admin = () => {
  const [show, setShow] = useState(false);
  const [add, setAdd] = useState(false);
  const [mem, setMem] = useState(false);
  const [admin, setAdmin] = useState(false);
  const products = useContext(ProductsContext);
  const [member, setMember] = useState({
    id:"",
    name:"",
    password:"",
    email:""

  });
  const delHandler = () => {
    setShow(!show);
    setAdmin(false);
    setMem(false);
    setAdd(false);
  };
  const addHandler = () => {
    setShow(false);
    setAdmin(false);
    setMem(false);
    setAdd(!add);
  };
  const memHandler = async () => {
    setShow(false);
    setAdmin(false);
    setMem(!mem);
    setAdd(false);
    const members = await axios.get(
      "https://shoping-cart.iran.liara.run/users"
    );
    setMember({id:members.data.id,name:members.data.name,});
  };
  const adminHandler = () => {
    setShow(false);
    setAdmin(!add);
    setMem(false);
    setAdd(false);
  };

  return (
    <div>
      <div className={style.admin}>
        <p>پنل ادمین</p>
        <button onClick={addHandler} className={style.a}>
          اضافه کردن محصول
        </button>
        <button onClick={delHandler} className={style.a}>
          حذف محصول
        </button>
        <button onClick={memHandler} className={style.a}>
          {" "}
          مدیریت کاربران
        </button>
        <button onClick={adminHandler} className={style.a}>
          {" "}
          مدیریت ادمین ها
        </button>
      </div>
      <div className={styles.container}>
        {show === true &&
          products.map((product) => (
            <HazfProduct key={product.id} productData={product} />
          ))}
        {add === true && <AddProduct />}
        {mem === true && <p>{member}</p>}
        {admin === true && <p>{}</p>}
      </div>
    </div>
  );
};

export default Admin;
