import React, { useContext, useState } from "react";
import style from "./Admin.module.css";
import { ProductsContext } from "../context/ProductContextProvider";
import styles from "./Store.module.css";
import HazfProduct from "./HazfProduct";
import AddProduct from "./AddProduct";
import axios from "axios";
import { Link } from "react-router-dom";

const Admin = () => {
  let del = "";
  const [show, setShow] = useState(false);
  const [add, setAdd] = useState(false);
  const [mem, setMem] = useState(false);
  const products = useContext(ProductsContext);
  const [member, setMember] = useState([]);
  const delHandler = () => {
    setShow(!show);
    setAdmin(false);
    setMem(false);
    setAdd(false);
  };
  const addHandler = () => {
    setShow(false);
    setAdmin(false);
    setMem(false);
    setAdd(!add);
  };
  const memHandler = async () => {
    setShow(false);
    setAdmin(false);
    setMem(!mem);
    setAdd(false);
    const members = await axios.get(
      "https://shoping-cart.iran.liara.run/users"
    );
    setMember(members.data);
  };


  return (
    <div className={style.mainContainer}>
      <div className={style.Continer}>
        <div className={style.admin}>
          <p> Admin</p>
          <button onClick={addHandler} className={style.a}>
            Add Product
          </button>
          <button onClick={delHandler} className={style.a}>
            Delete Product
          </button>
          <button onClick={memHandler} className={style.a}>
            Users
          </button>
          <button onClick={adminHandler} className={style.a}>
            Admins
          </button>
        </div>
        <div className={styles.container}>
          {show === true &&
            products.map((product) => (
              <HazfProduct key={product.id} productData={product} />
            ))}
          {add === true && <AddProduct />}

          {mem === true &&
            member.map((a) => (
              <div key={a._id}>
                <ul>
                  <li>{a._id}</li>
                  <li>{a.name}</li>
                  <li>{a.email}</li>
                  <li>{a.password}</li>

                  <button
                    className={style.b}
                    onClick={async () => {
                      del = a._id;
                      await axios.delete(
                        `https://shoping-cart.iran.liara.run/users/${del}`
                      );
                      window.location.reload();
                    }}
                  >
                    Delete
                  </button>
                </ul>
              </div>
            ))}
          {admin === true &&
            allAdmin.map((a) => (
              <div key={a._id}>
                <ul>
                  <li>{a.email}</li>
                  <li>{a.password}</li>
                </ul>
              </div>
            ))}

          <button className={style.c}>
            <Link to="/login"> Back </Link>
          </button>
        </div>
      </div>
    </div>
  );
};

export default Admin;
