import "./App.css";
import { Navigate, Route, Routes } from "react-router-dom";
import { GuardProvider, GuardedRoute } from "react-router-guards";

// Components
import Store from "./Foroshgah/components/Store";
import ProductDetails from "./Foroshgah/components/ProductDetails";
import Navbar from "./Foroshgah/components/shared/Navbar";
import ShopCart from "./Foroshgah/components/ShopCart";
import SignUp from "./login/components/SignUp";
import Login from "./login/components/Login";
import Home from "./Foroshgah/components/Home";

// Context
import ProductContextProvider from "./Foroshgah/context/ProductContextProvider";
import CartContextProvider from "./Foroshgah/context/CartContextProvider";
import Admin from "./Foroshgah/components/Admin";
import { useState } from "react";

function App() {

  const[isAutheticated, setisAutheticated] = useState(false);

  function login(){
    setisAutheticated(true);
    console.log("loggedInUser:" + isAutheticated)
  }

  function logout(){
    setisAutheticated(false);
    console.log("loggedInUser:" + isAutheticated)
  }
  const GuardedRoute = ({ component: Component, auth, ...rest }) => (
    <Route {...rest} render={(props) => (
        auth === true
            ? <Component {...props} />
            : <Navigate to='/' />
    )} />
)

  return (
    <ProductContextProvider>
      <CartContextProvider>
      <GuardedRoute path='/protected' component={Protected} auth={isAutheticated} />
          <Routes>
            <Route path="/products" element={<Store />} meta={{ auth: true }} />
            <Route path="/login" element={<Login />} meta={{ auth: true }} />
            <Route path="/admin" element={<Admin />} meta={{ auth: true }} />
            <Route path="/home" element={<Home />} meta={{ auth: true }} />
            <Route path="/signUp" element={<SignUp />} meta={{ auth: true }} />
            <Route
              path="/products/:id"
              element={<ProductDetails />}
              meta={{ auth: true }}
            />
            <Route path="/cart" element={<ShopCart />} meta={{ auth: true }} />
            <Route path="*" element={<Login />} />
          </Routes>

      </CartContextProvider>
    </ProductContextProvider>
  );
}

export default App;
