import React, { Component } from "react";
import Card from "./Card";
import styles from "./Cards.module.css";
import iphonex from "./iPhone-X.jpg";
import iphone11 from "./iphone-11.jpg";
import iphone12 from "./iphone-12.jpg";
import iphone13 from "./iphone-13.jpg";
import left from "../assets/icons/left.svg";
import right from "../assets/icons/right.svg";


class Cards extends Component {
  constructor() {
    super();
    this.state = {
      phoneData: [
        { id: 1, image: iphonex, name: "iphonex", cost: "150$" },
        { id: 2, image: iphone11, name: "iphone11", cost: "350$" },
        { id: 3, image: iphone12, name: "iphone12", cost: "500$" },
        { id: 4, image: iphone13, name: "iphone13", cost: "800$" },
      ],
    };
  }
  render() {
    return (
      <div className={styles.container}>

        {this.state.phoneData.map((phone) => {
          return (
            <>
 <button className={styles.arrow}><img src={left} alt="left"/></button> 
              <Card
                key={phone.id}
                image={phone.image}
                name={phone.name}
                cost={phone.cost}
              />
 <button className={styles.arrow}><img src={right} alt="right"/></button> 

            </>
          );
        })}
      </div>
    );
  }
}

export default Cards;
