import React, { useContext,useState } from 'react';
import { Link,navigate } from 'react-router-dom';
import axios from 'axios';


// Components
import Cart from './shared/Cart';

// Context
import { CartContext } from '../context/CartContextProvider';

// Style
import styles from "./ShopCart.module.css";

const ShopCart =async () => {
    const [data, setData] = useState({
        email: "",
        password: "",
      });

    const { state, dispatch } = useContext(CartContext);
    const loginHandler=async() =>{
        
    }
    await axios
    .post("https://shoping-cart.iran.liara.run/users/login", {
      email: data.email,
      password: data.password,
    })
    .then((response) => {
      if (response.data.status === false) {
        navigate("/");
      }
    });

    return (
        <div className={styles.container}>
            <div className={styles.cartContainer}>
                {state.selectedItems.map(item => <Cart key={item.id} data={item} />)}
            </div>

            {
                state.itemsCounter > 0 && <div className={styles.payments}>
                        <p><span>Total Items:</span> {state.itemsCounter}</p>
                        <p><span>Total Payments:</span> {state.total} $</p>
                        <div className={styles.buttonContainer}>
                            <button className={styles.clear} onClick={() => dispatch({type: "CLEAR"})}>Clear</button>
                            <button className={styles.checkout} onClick={() => dispatch({type: "CHECKOUT"})}>Checkout</button>
                        </div>
                    </div>
            }

            {
                state.itemsCounter === 0 && !state.checkout && <div className={styles.complete}>
                        <h3>Want to buy?</h3>
                        <Link to="/products">Go to shop</Link>
                    </div>
            }

            {
                state.checkout && <div className={styles.complete}>
                        <h3>Checked out successfully</h3>
                        <Link to="/products">Buy More</Link>
                    </div>
            }
            
        </div>
    );
};

export default ShopCart;
