/* eslint-disable no-const-assign */
import React, { useContext, useState } from "react";
import styles from "./Store.module.css";

import left from "../assets/icons/left.svg";
import right from "../assets/icons/right.svg";

import { ProductsContext } from "../context/ProductContextProvider";
import Card from "./Card";

const Cards = () => {
  const products = useContext(ProductsContext);
  const [numberMustShow, setnumberMustShow] = useState(4);
  const [numberFewShow, setnumberFewShow] = useState(1);

  const leftHandler = () => {
    setnumberMustShow((prevNumber) => prevNumber - 3);
    setnumberFewShow((prevNumber) => prevNumber - 3);
    if (numberFewShow < 0) {
      setnumberMustShow((prevNumber) => prevNumber === 3);
      setnumberFewShow((prevNumber) => prevNumber === 1);
    }
  };
  const rightHandler = () => {
    setnumberMustShow((prevNumber) => prevNumber + 4);
    setnumberFewShow((prevNumber) => prevNumber + 4);
    if (numberMustShow > 21) {
      setnumberMustShow((prevNumber) => prevNumber === 21);
      setnumberFewShow((prevNumber) => prevNumber === 16);
    }
  };

  const searchProduct = products.filter(
    (x) => numberFewShow < x.id && x.id < numberMustShow
  );

  return (
    <div className={styles.container}>
      <button className={styles.arrow} onClick={leftHandler}>
        <img src={left} alt="left" />
      </button>

      {searchProduct.map((product) => (
        <Card key={product.id} productData={product} />
      ))}
      <button className={styles.arrow}>
        <img src={right} alt="right" onClick={rightHandler} />
      </button>
    </div>
  );
};

export default Cards;
