import React, { useContext, useState } from "react";
import style from "./Admin.module.css";
import { ProductsContext } from "../context/ProductContextProvider";
import styles from "./Store.module.css";
import HazfProduct from "./HazfProduct";
import AddProduct from "./AddProduct";
import axios from "axios";
import { Link } from "react-router-dom";
import { Sidebar } from "primereact/sidebar";
import { Button } from "primereact/button";
import "primereact/resources/themes/lara-light-indigo/theme.css";
import "primereact/resources/primereact.min.css";
import "primeicons/primeicons.css";

const Admin = () => {
  let del = "";
  const [show, setShow] = useState(false);
  const [add, setAdd] = useState(false);
  const [mem, setMem] = useState(false);
  const products = useContext(ProductsContext);
  const [member, setMember] = useState([]);
  const [visibleRight, setVisibleRight] = useState(false);
  const delHandler = () => {
    setShow(!show);
    setMem(false);
    setAdd(false);
  };
  const addHandler = () => {
    setShow(false);
    setMem(false);
    setAdd(!add);
  };
  const memHandler = async () => {
    setShow(false);
    setMem(!mem);
    setAdd(false);
    const members = await axios.get(
      "https://shoping-cart.iran.liara.run/users"
    );
    setMember(members.data);
  };

  return (
    <div className="card">
      <Sidebar
        visible={visibleRight}
        position="right"
        onHide={() => setVisibleRight(false)}
      >
        <button onClick={addHandler} style={wei}>Add Product</button>
        <button onClick={delHandler}>Delete Product</button>
        <button onClick={memHandler}>Users</button>
      </Sidebar>

      <Button
        icon="pi pi-arrow-left"
        onClick={() => setVisibleRight(true)}
        className="mr-2"
      />
    </div>
  );
};

export default Admin;
