import React, { useState } from "react";

const AddProduct = () => {
  const [data, setData] = useState({
    name: "",
    number: "",
    detail: "",
    img: null,
  });
  const submitHandler = (event) => {
    event.preventDefault();
    setData({ name: event.target.value });
    console.log(data);
  };
  const changeHandler = (event) => {
    setData({ ...data, [event.target.name]: event.target.value });
  };

  return (
    <div>
      <form onSubmit={submitHandler}>
        <p>:عکس کالا</p>
        <input type="file" name="img" />
        <br></br>
        <p>نام کالا</p>
        <input type="text" name="name" value={data.name} onChange={changeHandler}/>
        <br></br>
        <p>هزینه</p>
        <input type="number" name="number" value={data.number} onChange={changeHandler}/>$<br></br>
        <p>جزئیات</p>
        <input type="text" name="detail" value={data.detail} onChange={changeHandler}/>
        <button type="submit">hi</button>
      </form>
    </div>
  );
};

export default AddProduct;
